import styled from "@emotion/styled"

const List = styled.ul`
  list-style-type: none;
  flex: 0 0 12rem;
  padding: 1rem;
  display: flex;
  justify-content: stretch;
  flex-direction: column;
  font-size: 0.75em;

  li {
    flex: 1;
  }

  li:nth-child(even) {
    opacity: 0.5;
  }
`

const ClockListItem = styled.li`
  position: absolute;
  top: ${props => props.progress}%;
  display: flex;
  padding: 0.5em;
  margin: -0.5em;
  background-color: var(--background);
  z-index: 1;
  box-shadow: 0 4px 2px var(--background);

  p + p {
    margin-left: 0.75em;
  }
`

function Clock(props) {
  const timer = useNewTimer(new Date())
  const interval = 1000 * 60 * 60 * 24
  const startOfDay = Math.floor(Date.now() / interval) * interval
  const elapsed = Math.abs(timer - startOfDay) / interval

  return (
    <ClockListItem progress={elapsed * 100}>
      <p>{timer.toLocaleTimeString(undefined, { timeStyle: "short" })}</p>
      <p>Usually very busy</p>
    </ClockListItem>
  )
}

function useNewTimer(currentDate) {
  const [date, setDate] = React.useState(currentDate)

  React.useEffect(() => {
    var timerID = setInterval(() => tick(), 1000)
    return function cleanup() {
      clearInterval(timerID)
    }
  })

  function tick() {
    setDate(new Date())
  }

  return date
}

export default function Timeline() {
  const timestamps = Array(24)
    .fill(undefined)
    .map((_, i) => `${i}`.padStart(2, "0"))

  const currentTime = Date.now

  return (
    <List>
      <Clock />
      {timestamps.map(timestamp => (
        <React.Fragment>
          <li>{timestamp}:00</li>
          <li>{timestamp}:30</li>
        </React.Fragment>
      ))}
    </List>
  )
}
